var recurrence = K('field[Recurrence]').value(),
    textArray = ["Every"],
    weeklyArray = [],
    daysDisplay = [];

switch (recurrence)
{
  case 'minutely':
    textArray.push( values('Interval') );
    textArray.push( values('Interval') === "1" ? "minute" : "minutes" );
    break;
  case 'hourly':
    textArray.push( values('Interval') );
    textArray.push( values('Interval') === "1" ? "hour" : "hours" );
    textArray.push( "at" );
    textArray.push( ":" + values('Execution Minute') + " past the hour");
    break;
  case 'daily':
    textArray.push( values('Interval') );
    textArray.push( values('Interval') === "1" ? "day" : "days" );
    textArray.push( "at" );
    textArray.push( values('Execution Hour') + ":" + values('Execution Minute') + " UTC" );
    break;
  case 'weekly':
    textArray.push( values('Interval') );
    textArray.push( values('Interval') === "1" ? "week" : "weeks" );
    textArray.push( "on" );
    textArray.push( values('Weekdays').join(" and " ) );
    textArray.push( "at" );
    textArray.push( values('Execution Hour') + ":" + values('Execution Minute') + " UTC" );
    break;
  case 'monthly':
    textArray.push( values('Interval') );
    textArray.push( values('Interval') === "1" ? "month" : "months" );
    textArray.push( "on the" );
    if ( values('Timing') === "absolute" ) {
      $.each(values('Days'),function(key,value){
        // values ending in 11, 12, 13 are always "th".  Otherwise values ending in 1=1st, 2=2nd, 3=3rd; "Last" ends with 't'.
        switch ( ["11", "12", "13"].indexOf(value.slice(-2)) > -1 ? "th" : value.slice(-1) )
        {
          case '1':
            daysDisplay.push(value + "st");
            break;
          case '2':
            daysDisplay.push(value + "nd");
            break;
          case '3':
            daysDisplay.push(value + "rd");
            break;
          case 't':
            daysDisplay.push(value + " day");
            break;
          default:
            daysDisplay.push(value + "th");
        }
      })
      textArray.push( daysDisplay.join( ", " ) );
    }
    else {
      $.each( values('Weekdays'), function(key, value) {
        weeklyArray.push( values( value + ' Index').join( ", " ) + " " + value );
      } );
      textArray.push( weeklyArray.join(", and ") );
    }
    textArray.push( "at" );
    textArray.push( values('Execution Hour') + ":" + values('Execution Minute') + " UTC" );
    break;
  case 'yearly':
    textArray.push( values('Interval') );
    textArray.push( values('Interval') === "1" ? "year" : "years" );
    textArray.push( "in" );
    textArray.push( values('Months').join(" and " ) );
    textArray.push( "on the" );
    if ( values('Timing') === "absolute" ) {
      $.each(values('Days'),function(key,value){
        // values ending in 11, 12, 13 are always "th".  Otherwise values ending in 1=1st, 2=2nd, 3=3rd; "Last" ends with 't'.
        switch ( ["11", "12", "13"].indexOf(value.slice(-2)) > -1 ? "th" : value.slice(-1) )
        {
          case '1':
            daysDisplay.push(value + "st");
            break;
          case '2':
            daysDisplay.push(value + "nd");
            break;
          case '3':
            daysDisplay.push(value + "th");
            break;
          case 't':
            daysDisplay.push(value + " day");
            break;
          default:
            daysDisplay.push(value);
        }
      })
      textArray.push( daysDisplay.join( ", " ) );
    }
    else {
      $.each( values('Weekdays'), function(key, value) {
        weeklyArray.push( values( value + ' Index').join( ", " ) + " " + value);
      } );
      textArray.push( weeklyArray.join(", and ") );
    }
    textArray.push( "at" );
    textArray.push( values('Execution Hour') + ":" + values('Execution Minute') + " UTC" );
    break;
  default:
    textArray.push( "Unknown" );
}
K('field[Recurrence Description]').value( textArray.join(" ") );
