loadingDetails=true;
K('bridgedResource[Get Member]').load({
  attributes: ['Member ID', 'First Name', 'Last Name'],
  values: {'ID' : 'Allen'},
  success: function(bridgedData) {
    K('field[Member ID]').value(bridgedData['Member ID']);
    K('field[Student First Name]').value(bridgedData['First Name']);
    K('field[Student Last Name]').value(bridgedData['Last Name']);
    K('field[Billing Customer Id]').value(bridgedData['Billing Customer Id']);

    $.ajax({
      type: "post",
        url: K('space').attributes("Kinetic Billing Server URL")+"/billingInfo",
        data: JSON.stringify({ 
          customerId: bridgedData['Billing Customer Id'],
          space: K('space').slug(),
          billingService: K('space').attributes("Billing Company"),
        }),
        headers: {
          'Accept': 'application/json, text/plain, */*',
          'Content-Type': 'application/json;charset=UTF-8'
      	}
      })
      .done(function( data ) {
        if (data.data===null) {
  	      K('field[The last debit will be taken on]').value("");
          K('field[Billing Period]').value("");
        } else {
  	      K('field[The last debit will be taken on]').value(moment(data.data['nextBillingDate'],'DD-MM-YYYY').format('YYYY-MM-DD'));
          K('field[Billing Period]').value(data.data['paymentPeriod']);

          bundle.config.widgets.xdsoftDatepickerRemove({
              element: K("form").find("#lastdebitdate")[0]
          });          
          period=7;
          if (data.data['paymentPeriod']==="Fortnightly"){
            period=14;
          }
          allowDates=[];
          for (i=0;i<20;i++){
            if (moment(data.data['nextBillingDate'], "DD-MM-YYYY").add(period*i,"days").isAfter(moment())) {
  						  allowDates[allowDates.length]=moment(data.data['nextBillingDate'], "DD-MM-YYYY").add(period*i,"days").format("DD-MM-YYYY");
            }
  				}
          debugger;
          bundle.config.widgets.xdsoftDatepicker({
              element: K("form").find("#lastdebitdate")[0],
              timepicker: false,
              displayDateFormat: "DD/MM/YYYY",
              options: {
                allowDates: allowDates,
                formatDate:"d-m-Y",
                validateOnBlur:false
              },
              onSelectDate:function(ct,$i){
                K('field[The last debit will be taken on]').value(ct.dateFormat('YYYY-MM-DD'));
              }                
          });          
         }
      });
    loadingDetails=false;

    K('section[Member Select Space]').hide();    
    }
});

